(function(angular) {
    'use strict';

    define([
        'angular',
        'bar'
    ], function() {
        // Module declaration. The app module is the main entry point for the entire app.
        angular.module('app', ['bar']);
        // Called the app module.
        var app = angular.module('app');

        var appConfigInjectables = ['$compileProvider'];
        function appConfig($compileProvider) {
            // Disable ng-scope and other debug code generated by angular. Comment this line if you wish to enable angular debug thingy.
            $compileProvider.debugInfoEnabled(false);
        }
        app.config(appConfigInjectables.concat([appConfig]));

        angular.element(document).ready(function() {
            return angular.bootstrap(document, ['app']);
        });
    });
})(angular);
